import {Link} from 'react-router-dom';
import React, {useState, useEffect} from 'react';


class Home extends Component {
    constructor() {
        super();
        this.state = { items: [] };
        this.state= {movementsList:[]}
    }

    componentDidMount() {
        fetch('movements/api/list/')
        .then(res => res.json())
        .then(data => {
          console.log(data);
          this.setState({
            movementsList:data
          })
        })
        .catch(console.log)
      }

    render() {
        return (
          <div className="container">
              <h1>App de finanzas</h1>

              {/* Balance */}
              <h2>Balance:</h2>


              {/* Last 10 movements */}
              <h2>Ultimos movimientos:</h2>
              {this.state.movementsList.map((aMovement) => (
                      <tr>  
                            <td>
                                {aMovement.input_date.split("T")[0].split('-').reverse().join('/')}
                            </td>
                            <td>
                                {aMovement.concept}
                            </td>
                            <td>
                                {aMovement.amount}
                            </td>
                            <td>
                                {aMovement.input_type}
                            </td>
                      </tr>
              )
         )
    }
        </div>
    )}
}

export default function Movements(){
    const [movements, setMovements] = useState([]);
    useEffect(()=>{
        fetch('/movements/api/').then((result)=>result.json()).then((json)=>{
            console.log({json});
            setMovements(json);
        }).catch(console.log);
    }, []);

    return (<div>      

        <h2>Movimientos: </h2>

        <div class="row justify-content-center">     
        <div class="col-8">
            <table class="table table-borderless table table-hover">
                <thead>
                     <tr>
                        <th scope="col">Date</th>
                        <th scope="col">Concept</th>
                        <th scope="col">Amount</th>
                        <th scope="col">Type</th>
                    </tr>
                </thead>
                <tbody>
                    {movements.map(aMovement=>
                        <tr>  
                            <td>
                                {aMovement.input_date.split("T")[0].split('-').reverse().join('/')}
                            </td>
                            <td>
                                {aMovement.concept}
                            </td>
                            <td>
                                {aMovement.amount}
                            </td>
                            <td>
                                {aMovement.input_type}
                            </td>

                            <td>
                            {/* Delete item */}
                            <button type="button" class="btn btn-primary btn-block" onClick={()=>{
                                fetch(`/movements/api/${aMovement.input_id}`,{method:'DELETE'}).then(()=>{
                                    fetch('/movements/api').then((result)=>result.json()).then((json)=>{
                                        console.log({json});
                                        setMovements(json);
                                        }).catch(console.log);
                                    })
                            }}>Eliminar</button>
                            </td>

                            <td>
                            {/* Edit item */}
                            <button type="button" class="btn btn-primary btn-block" onClick={()=>{
                                fetch(`/movements/api/${aMovement.input_id}`,{method:'UPDATE'}).then(()=>{
                                    fetch('/movements/api').then((result)=>result.json()).then((json)=>{
                                        console.log({json});
                                        setMovements(json);
                                    }).catch(console.log);
                                })
                            }}>Editar</button>
                            </td>
                        </tr> 
                    )}                     
                    </tbody>
            </table>
        </div>
        </div>

        {/* Order by type */}
        <div class="container">
            <div class="row justify-content-center">
                <div class="col-6">
                    <form action="/movements/api" method="get">
                        <div class="form-group">
                            <label for="type">Ordenar por:</label>
                            <select class="form-control" id="type" name="type">
                                <option>Income</option>
                                <option>Expense</option>
                            </select>
                        </div>
                    </form>
                </div>
            </div>
        </div>
            {/*
            <button type="button" class="btn btn-primary btn-block" onClick={()=>{
                fetch(`/movements/api/${aMovement.input_type}`).then(()=>{
                    fetch('/movements/api').then((result)=>result.json()).then((json)=>{
                        console.log({json});
                        setMovements(json);
                    }).catch(console.log);
                })
            }}>Ordenar</button>*/}
        
        {/* Back to Home */}
        <div>
            <Link to= "/"><button type="button" class="btn btn-primary btn-block">Volver a Home</button></Link>
        </div>

    </div>)};
