import {Link} from 'react-router-dom';
import React, {Component} from 'react';

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = { input: '' };
  }

  handleChange = (event) => {
    this.setState({[event.target.input]: event.target.value});
  }

  handleSubmit = (event) => {
    alert('An input was submitted: ' + this.state);

    fetch('movements/api/', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify(this.state)
        }).then(function(response) {
        console.log(response)
        return response.json();
      });

    event.preventDefault();
}

  render() {
    return (
    <div class="container">
    <div class="row justify-content-center">
    <div class="col-6">
      <form onSubmit={this.handleSubmit}>
               <div class="form-group">
                    <label for="concept">Concept:</label>
                    <input type="text" name="concept" id="concept" class="form-control" value={this.state.value} onChange={this.handleChange}/>
                </div>
                <div class="form-group">
                    <label for="amount">Amount:</label>
                        <div class="input-group-prepend">
                            <span class="input-group-text">$</span>
                            <input type="number" name="amount" id="amount" class="form-control" value={this.state.value} onChange={this.handleChange}/>
                        </div>
                </div>
                <div class="form-group">
                    <label for="date">Date:</label>
                    <input type="date" name="date" id="date" class="form-control" value={this.state.value} onChange={this.handleChange}/>
                </div>
                <div class="form-group">
                    <label for="type">Type:</label>
                    <select class="form-control" id="type" name="type" value={this.state.value} onChange={this.handleChange}>
                        <option>Income</option>
                        <option>Expense</option>
                    </select>
                </div>
                <input type="submit" value="Submit" />
      </form>
    </div>
    </div>
    </div>
    );
  }
}

