import {Link} from 'react-router-dom';
import React, {Component} from 'react';
import Axios from 'axios';

class Home extends Component {
    constructor() {
        super();
        this.state= {
            movementsList:[],
            balance: '',
        }
    }

    componentDidMount() {
        //get list
        fetch('/movements/api/list/')
        .then(res => res.json())
        .then(data => {
          console.log(data);
          this.setState({
            movementsList:data
          })
        })
        .catch(console.log)

        //get balance
        Axios.get('/movements/api/balance/').then((response)=>{
            this.setState({
                balance: response.data.balance })
        })
    }

    render() {
        return (
        <div id="viewport">

            {/*} Sidebar */}
    <div class="bg-light border-right" id="sidebar-wrapper">
      <div class="sidebar-heading">Start Bootstrap </div>
      <div class="list-group list-group-flush">
        <a href="#" class="list-group-item list-group-item-action bg-light">Dashboard</a>
        <a href="#" class="list-group-item list-group-item-action bg-light">Shortcuts</a>
        <a href="#" class="list-group-item list-group-item-action bg-light">Overview</a>
        <a href="#" class="list-group-item list-group-item-action bg-light">Events</a>
        <a href="#" class="list-group-item list-group-item-action bg-light">Profile</a>
        <a href="#" class="list-group-item list-group-item-action bg-light">Status</a>
      </div>
    </div>
            <div id="sidebar">
            <nav className="navbar navbar-default">
                <header>
                <a href="#">Mis finanzas</a>
                </header>
                <ul className="nav">
                <li>
                    <Link to= "/"><i className="zmdi zmdi-view-dashboard"></i> Balance</Link>
                </li>
                <li>
                    <Link to= "/form"><i className="zmdi zmdi-link"></i> Ingresar movimiento</Link>
                </li>
                <li>
                    <Link to= "/movements"><i className="zmdi zmdi-widgets"></i>Ver movimientos</Link>
                </li>
                </ul>
            </nav>
            </div>
            
            {/* Content */}
            <div className = "col-auto bg-danger p5 text-center"></div>)
            <h3 className="user">Usuario</h3>

            {/* Balance */}
            <div class="container-fluid" class="div_balance">
                <h2>Balance:</h2>
                <h2>{this.state.balance}</h2>
            </div>

            {/* Last 10 movements */}
            <div clasName = "tablediv">
                <h2>Ultimos movimientos:</h2>
                    <div className="row justify-content-center">     
                    <div className="col-8">
                        <table className="table table-borderless table table-hover">
                            <thead>
                                <tr>
                                    <th scope="col">Fecha</th>
                                    <th scope="col">Concepto</th>
                                    <th scope="col"></th>
                                    <th scope="col">Valor</th>
                                    <th scope="col">Tipo</th>
                                    <th scope="col">Tipo de gasto:</th>
                                </tr>
                            </thead>
                            <tbody>
                            {this.state.movementsList.map((aMovement) => (
                            <tr>  
                                    <td>
                                        {aMovement.input_date.split("T")[0].split('-').reverse().join('/')}
                                    </td>
                                    <td>
                                        {aMovement.concept}
                                    </td>
                                    <td>
                                        {aMovement.input_type=="Expense"?"-":"+"}
                                    </td>
                                    <td>
                                        {aMovement.amount}
                                    </td>
                                    <td>
                                        {aMovement.input_type=="Income"?"Ingreso":"Gasto"}
                                    </td>
                                    <td>
                                        {aMovement.type_expense}
                                    </td>
                            </tr>
                                    )
                                )
                            }
                            </tbody>
                            </table>
                        </div>
                        </div>
                </div>
        </div>
        )};
}

export default Home
