import React, {Component} from 'react';


class Home extends Component {
    constructor() {
        super();
        this.state = { items: [] };
        this.state= {movementsList:[]}
        this.state= {balanceValue:[]}
    }

    componentDidMount() {
        fetch('movements/api/list/')
        .then(res => res.json())
        .then(data => {
          console.log(data);
          this.setState({
            movementsList:data
          })
        })
        .catch(console.log)
      }

      componentDidMount() {
        fetch('movements/api/balance/')
        .then(res => res.json())
        .then(info => {
          console.log(info);
          this.setState({
            balanceValue:data
          })
        })
        .catch(console.log)
      }

    render() {
        return (
          <div className="container">
              <h1>App de finanzas</h1>
              <h2>Balance:</h2>
                {this.state.balanceValue.map((aBalance) => (
                      <div>  
                          {aBalance}
                      </div>
                      )
                )
              }
              <h2>Ultimos movimientos:</h2>
              {this.state.movementsList.map((aMovement) => (
                      <tr>  
                            <td>
                                {aMovement.input_date.split("T")[0].split('-').reverse().join('/')}
                            </td>
                            <td>
                                {aMovement.concept}
                            </td>
                            <td>
                                {aMovement.amount}
                            </td>
                            <td>
                                {aMovement.input_type}
                            </td>
                      </tr>
                      )
                )
              }
     </div>
    )}
}

export default Home
