import {Link} from 'react-router-dom';
import React, {Component} from 'react';

class Form extends Component {

constructor(props) {
    super(props);
    this.state = {
        concept : '',
        amount: '',
        input_date: '',
        input_type: '',
    }

    this.handleChangeConcept= this.handleChangeConcept.bind(this);
    this.handleChangeAmount= this.handleChangeAmount.bind(this);
    this.handleChangeInput_date= this.handleChangeInput_date.bind(this);
    this.handleChangeInput_type= this.handleChangeInput_type.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);

};

handleChangeConcept(event) {
    this.setState({
        concept: event.target.value,
    })
};

handleChangeAmount(event) {
    this.setState({
        amount: event.target.value,
    })
};

handleChangeInput_date(event) {
    this.setState({
        input_date: event.target.value,
    })
};

handleChangeInput_type(event) {
    this.setState({
        input_type: event.target.value,
    })
};

handleSubmit(event) {
    fetch('movements/api/', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            concept : this.state.conceptt,
            amount: this.state.amount,
            input_date: this.state.input_date,
            input_type: this.state.input_type,
        })
    });
    event.preventDefault();
};

  render() {
    return (
    <div class="container">
    <div class="row justify-content-center">
    <div class="col-6">
      <form onSubmit={this.handleSubmit}>
               <div class="form-group" controlId="formBasicAmount" >
                    <label for="concept">Concept:</label>
                    <input type="text" name="concept" id="concept" class="form-control" onChange={this.handleChangeConcept}/>
                </div>
                <div class="form-group" controlId="formBasicAmount">
                    <label for="amount">Amount:</label>
                        <div class="input-group-prepend">
                            <span class="input-group-text">$</span>
                            <input type="number" name="amount" id="amount" class="form-control" onChange={this.handleChangeAmount}/>
                        </div>
                </div>
                <div class="form-group">
                    <label for="date">Date:</label>
                    <input type="date" name="date" id="date" class="form-control" onChange={this.handleChangeInput_date}/>
                </div>
                <div class="form-group">
                    <label for="type">Type:</label>
                    <select class="form-control" id="type" name="type" onChange={this.handleChangeInput_type}>
                        <option>Income</option>
                        <option>Expense</option>
                    </select>
                </div>
                <button type="submit" class="btn btn-primary btn-block" value="Submit">Submit</button>
      </form>
    </div>
    </div>
        <div>
        <Link to= "/"><button type="button" class="btn btn-primary btn-block" onClick={this.handleSubmit}>Home</button></Link>
    </div>
    </div>
    
/*Cierre de la funcion no tocar */
    )
  }
}

export default Form
