import {Link} from 'react-router-dom';
import React, {useState, useEffect} from 'react';

export default function Movements(){
    const [movements, setMovements] = useState([]);
    useEffect(()=>{
        fetch('/movements/api').then((result)=>result.json()).then((json)=>{
            console.log({json});
            setMovements(json);
        }).catch(console.log);
    }, []);

    return (<div>      
      
            {movements.map(aMovement=>
            <div>{aMovement.input_date},{aMovement.concept},{aMovement.amount}, {aMovement.input_type}

            {/* Delete item */}
            <button type="button" class="btn btn-primary btn-block" onClick={()=>{
                fetch(`/movements/api/${aMovement.input_id}`,{method:'DELETE'}).then(()=>{
                    fetch('/movements/api').then((result)=>result.json()).then((json)=>{
                        console.log({json});
                        setMovements(json);
                    }).catch(console.log);
                })
            }}>Eliminar</button>
   
            {/* Edit item */}
            <button type="button" class="btn btn-primary btn-block" onClick={()=>{
                fetch(`/movements/api/${aMovement.input_id}`,{method:'UPDATE'}).then(()=>{
                    fetch('/movements/api').then((result)=>result.json()).then((json)=>{
                        console.log({json});
                        setMovements(json);
                    }).catch(console.log);
                })
            }}>Editar</button>
            </div>)}
            
            {/* Order by type */}
            <div class="container">
                <div class="row justify-content-center">
                    <div class="col-6">
                        <form action="/movements/api" method="get">
                            <div class="form-group">
                                <label for="type">Ordenar por:</label>
                                <select class="form-control" id="type" name="type">
                                    <option>Income</option>
                                    <option>Expense</option>
                                </select>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
            {/*
            <button type="button" class="btn btn-primary btn-block" onClick={()=>{
                fetch(`/movements/api/${aMovement.input_type}`).then(()=>{
                    fetch('/movements/api').then((result)=>result.json()).then((json)=>{
                        console.log({json});
                        setMovements(json);
                    }).catch(console.log);
                })
            }}>Ordenar</button>*/}
        
        {/* Back to Home */}
        <div>
            <Link to= "/"><button type="button" class="btn btn-primary btn-block">Volver a Home</button></Link>
        </div>

    </div>)};
