import {Link} from 'react-router-dom';
import React, {useState, useEffect} from 'react';

export default function Home(){
    const [movements, setMovements] = useState([]);
    useEffect(()=>{
        fetch('/movements/api').then((result)=>result.json()).then((json)=>{
            console.log({json});
            setMovements(json);
            }).catch(console.log);
    }, []);

    return (<div>
        <h1>App de finanzas</h1>

        {/* Balance */}
            <h2>Balance neto:</h2>

        {/* Last 10 movements */}
            <h2>Ultimos movimientos:</h2>

            
            <div class="row justify-content-center">     
            <div class="col-8">
                <table class="table table-borderless table table-hover">
                    <thead>
                        <tr>
                            <th scope="col">Date</th>
                            <th scope="col">Concept</th>
                            <th scope="col">Amount</th>
                            <th scope="col">Type</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            {movements.map(aMovement=>
                                <th scope="row">{aMovement.input_date.split("T")[0].split('-').reverse().join('/')}</th>,
                                <div>
                                {aMovement.concept},
                                {aMovement.amount},
                                {aMovement.input_type}
                                </div>
                            )}                       
                            <td>Sit</td>
                            <td>Amet</td>
                            <td>Consectetur</td>
                        </tr>
                    </tbody>
                </table>
            </div>
            </div>


        {/* Buttons */}
        <div>
            <Link to=  "/form" ><   button type="button" class="btn btn-primary btn-block">Ingresar movimiento</button></Link>
            <br/>    
            <Link to="/movements"><button type="button" class="btn btn-primary btn-block">Ver movimientos</button></Link>
        </div>
    </div>);
};
