import {Link} from 'react-router-dom';
import React, {Component} from 'react';
import Axios from 'axios';

class Home extends Component {
    constructor() {
        super();
        this.state= {
            movementsList:[],
            balance: '',
        }
    }

    componentDidMount() {
        //get list
        fetch('/movements/api/list/')
        .then(res => res.json())
        .then(data => {
          console.log(data);
          this.setState({
            movementsList:data
          })
        })
        .catch(console.log)

        //get balance
        Axios.get('/movements/api/balance/').then((response)=>{
            this.setState({
                balance: response.data.balance })
        })
    }

    render() {
        return (
        <div id="wrapper">

            {/* Sidebar */}
            <div class="sidebar-container">
            <div class="sidebar-logo">Mis finanzas</div>
                <ul class="sidebar-navigation">
                    <li>
                        <Link to="/"><i class="fa fa-home" aria-hidden="true">Home</i></Link>
                    </li>
                    <li>
                        <Link href="#">
                            <i class="fa fa-tachometer" aria-hidden="true"></i>Ingresar movimiento</Link>
                    </li>
                </ul>
            </div>

            <div class="content-container">
            <div class="container-fluid">
           {/* Page Content */}
            <div className="content">
                                {/* Balance */}
                                <div class="container-fluid" class="div_balance">
                                    <h2>Balance:</h2>
                                    <h2>{this.state.balance}</h2>
                                </div>

                                {/* Last 10 movements */}
                                <div clasName = "tablediv">
                                    <h2>Ultimos movimientos:</h2>
                                        <div className="row justify-content-center">     
                                        <div className="col-8">
                                            <table className="table table-borderless table table-hover">
                                                <thead>
                                                    <tr>
                                                        <th scope="col">Fecha</th>
                                                        <th scope="col">Concepto</th>
                                                        <th scope="col"></th>
                                                        <th scope="col">Valor</th>
                                                        <th scope="col">Tipo</th>
                                                        <th scope="col">Tipo de gasto:</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                {this.state.movementsList.map((aMovement) => (
                                                <tr>  
                                                        <td>
                                                            {aMovement.input_date.split("T")[0].split('-').reverse().join('/')}
                                                        </td>
                                                        <td>
                                                            {aMovement.concept}
                                                        </td>
                                                        <td>
                                                            {aMovement.input_type=="Expense"?"-":"+"}
                                                        </td>
                                                        <td>
                                                            {aMovement.amount}
                                                        </td>
                                                        <td>
                                                            {aMovement.input_type=="Income"?"Ingreso":"Gasto"}
                                                        </td>
                                                        <td>
                                                            {aMovement.type_expense}
                                                        </td>
                                                </tr>
                                                        )
                                                    )
                                                }
                                                </tbody>
                                                </table>
                                        </div>
                                        </div>
                                </div>
            </div>

            </div>
            </div>
            
 

{/* Cierre de la funcion */}
        </div>)
}}

export default Home
