import {Link} from 'react-router-dom';
import React, {Component} from 'react';
import Axios from 'axios';

class Home extends Component {
    constructor() {
        super();
        this.state= {
            movementsList:[],
            balance: '',
        }
    }

    componentDidMount() {
        //get list
        fetch('/movements/api/list/')
        .then(res => res.json())
        .then(data => {
          console.log(data);
          this.setState({
            movementsList:data
          })
        })
        .catch(console.log)

        //get balance
        Axios.get('/movements/api/balance/').then((response)=>{
            this.setState({
                balance: response.data.balance })
        })
    }

    render() {
        return (
        <div id="wrapper">

            {/* Sidebar */}
            <body id="body-pd">
                <header class="header" id="header">
                    <div class="header_toggle"> <i class='bx bx-menu' id="header-toggle"></i> </div>
                    <div class="header_img"> <img src="https://i.imgur.com/hczKIze.jpg" alt=""/> </div>
                </header>
                <div class="l-navbar" id="nav-bar">
                    <nav class="nav">
                        <div> <a href="#" class="nav_logo"> <i class='bx bx-layer nav_logo-icon'></i> <span class="nav_logo-name">BBBootstrap</span> </a>
                            <div class="nav_list"> <a href="#" class="nav_link active"> <i class='bx bx-grid-alt nav_icon'></i> <span class="nav_name">Dashboard</span> </a> <a href="#" class="nav_link"> <i class='bx bx-user nav_icon'></i> <span class="nav_name">Users</span> </a> <a href="#" class="nav_link"> <i class='bx bx-message-square-detail nav_icon'></i> <span class="nav_name">Messages</span> </a> <a href="#" class="nav_link"> <i class='bx bx-bookmark nav_icon'></i> <span class="nav_name">Bookmark</span> </a> <a href="#" class="nav_link"> <i class='bx bx-folder nav_icon'></i> <span class="nav_name">Files</span> </a> <a href="#" class="nav_link"> <i class='bx bx-bar-chart-alt-2 nav_icon'></i> <span class="nav_name">Stats</span> </a> </div>
                        </div> <a href="#" class="nav_link"> <i class='bx bx-log-out nav_icon'></i> <span class="nav_name">SignOut</span> </a>
                    </nav>
                </div>
                {/* Container Main start */}
                <div class="height-100 bg-light">
                    {/* Page Content */}
                    <div className="content">
                                {/* Balance */}
                                <div class="container-fluid" class="div_balance">
                                    <h2>Balance:</h2>
                                    <h2>{this.state.balance}</h2>
                                </div>

                                {/* Last 10 movements */}
                                <div clasName = "tablediv">
                                    <h2>Ultimos movimientos:</h2>
                                        <div className="row justify-content-center">     
                                        <div className="col-8">
                                            <table className="table table-borderless table table-hover">
                                                <thead>
                                                    <tr>
                                                        <th scope="col">Fecha</th>
                                                        <th scope="col">Concepto</th>
                                                        <th scope="col"></th>
                                                        <th scope="col">Valor</th>
                                                        <th scope="col">Tipo</th>
                                                        <th scope="col">Tipo de gasto:</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                {this.state.movementsList.map((aMovement) => (
                                                <tr>  
                                                        <td>
                                                            {aMovement.input_date.split("T")[0].split('-').reverse().join('/')}
                                                        </td>
                                                        <td>
                                                            {aMovement.concept}
                                                        </td>
                                                        <td>
                                                            {aMovement.input_type=="Expense"?"-":"+"}
                                                        </td>
                                                        <td>
                                                            {aMovement.amount}
                                                        </td>
                                                        <td>
                                                            {aMovement.input_type=="Income"?"Ingreso":"Gasto"}
                                                        </td>
                                                        <td>
                                                            {aMovement.type_expense}
                                                        </td>
                                                </tr>
                                                        )
                                                    )
                                                }
                                                </tbody>
                                                </table>
                                        </div>
                                        </div>
                                </div>
                    </div>
                        </div>



{/* Cierre de la funcion */}
        </body>
        </div>)
}}

export default Home
