import {Link} from 'react-router-dom';
import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import {useParams} from 'react-router';

function Form(){
    const [concept, setConcept] = useState('');
    const [amount, setAmount] = useState('');
    const [input_date, setInput_date] = useState('');
    const [input_type, setInput_type] = useState('Income'); //valor por defecto, el primero del select

    //Para cuando edita
    const {input_id} = useParams();
    useEffect(()=>{
        if (input_id && input_id.length){
            //si no es nulo, carga los valores existentes
            Axios.get(`/movements/api/${input_id}`).then((response)=>{
                    console.log(response);
                    if (response.data.length){
                        const [value] = response.data; //toma el primer valor (es el primer valor por la sintaxis)
                        set
                    }
            }
            )
        }
    },[input_id])

    const submitForm = (e)=>{
        e.preventDefault(); //para que no recargue la pag
        Axios.post('/movements/api/import/',{
            concept: concept,
            amount: amount,
            input_date: input_date,
            input_type: input_type
            }).then(()=>{
                alert('Movimiento ingresado');
                //para limpiar el formulario
                setConcept('');
                setAmount('');
                setInput_date('');
                setInput_type('Income');
            }).catch(e=>console.log(e))
    }

    return (
    <div>
        <div className="container">
        <div className="row justify-content-center">
        <div className="col-6">
        <form onSubmit={submitForm}>
                <div className="form-group">
                        <label htmlFor="concept">Concepto:</label>
                        <input type="text" name="concept" id="concept" value={concept} className="form-control" onChange={(e)=>{setConcept(e.target.value)}}/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Valor:</label>
                            <div className="input-group-prepend">
                                <span className="input-group-text">$</span>
                                <input type="number" name="amount" id="amount" value={amount} className="form-control"  onChange={(e)=>{setAmount(e.target.value)}}/>
                            </div>
                    </div>
                    <div className="form-group">
                        <label htmlFor="input_date">Fecha:</label>
                        <input type="date" name="input_date" id="input_date" className="form-control" value={input_date} onChange={(e)=>{setInput_date(e.target.value)}}/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="input_type">Tipo:</label>
                        <select  type="text" name="input_type" id="input_type" className="form-control" value={input_type} onChange={(e)=>{setInput_type(e.target.value)}}>
                            <option >Ingreso</option>
                            <option>Gasto</option>
                        </select>
                    </div>
                    <button type="submit" className="btn btn-primary btn-block" value="Submit">Aceptar</button>
        </form>
        </div>
        </div>
        </div>

        <Link to= "/"><button type="button" className="btn btn-primary btn-block" >Volver a Home</button></Link>

{/*Cierre de la funcion no tocar */}
    </div>)
};

export default Form
