import {Link} from 'react-router-dom';
import React, {Component} from 'react';

class Form extends Component {

constructor(props) {
    super(props);
    this.state = {
        concept: '',
        amount: '',
        input_date: '',
        input_type: '',
    }
};

/*
    this.handleChangeConcept= this.handleChangeConcept.bind(this);
    this.handleChangeAmount= this.handleChangeAmount.bind(this);
    this.handleChangeInput_date= this.handleChangeInput_date.bind(this);
    this.handleChangeInput_type= this.handleChangeInput_type.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);

};*/

handleChange(property) {
    var self = this;
    return function _handleChange(event) {
        self.setState({[property]: event.target.value});
    }
};

/*
handleChangeConcept(event) {
    this.setState({
        concept: event.target.value,
    })
};

handleChangeAmount(event) {
    this.setState({
        amount: event.target.value,
    })
};

handleChangeInput_date(event) {
    this.setState({
        input_date: event.target.value,
    })
};

handleChangeInput_type(event) {
    this.setState({
        input_type: event.target.value,
    })
};*/

handleSubmit(event) {
    fetch('movements/api/', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            concept : this.state.concept,
            amount: this.state.amount,
            input_date: this.state.input_date,
            input_type: this.state.input_type,
        })
    });
    event.preventDefault();
};

  render() {
    return (
    <div className="container">
    <div className="row justify-content-center">
    <div className="col-6">
      <form onSubmit={this.handleSubmit}>
               <div className="form-group">
                    <label htmlFor="concept">Concept:</label>
                    <input type="text" name="concept" id="concept" className="form-control" onChange={this.handleChange('concept')}/>
                </div>
                <div className="form-group">
                    <label for="amount">Amount:</label>
                        <div class="input-group-prepend">
                            <span class="input-group-text">$</span>
                            <input type="number" name="amount" id="amount" class="form-control"  onChange={this.handleChange('amount')}/>
                        </div>
                </div>
                <div class="form-group">
                    <label for="input_date">Date:</label>
                    <input type="date" name="input_date" id="input_date" class="form-control"  onChange={this.handleChange('input_date')}/>
                </div>
                <div class="form-group">
                    <label for="input_type">Type:</label>
                    <select  type="text" name="input_type" id="input_type" class="form-control"  onChange={this.handleChange('input_type')}>
                        <option >Income</option>
                        <option>Expense</option>
                    </select>
                </div>
                <button type="submit" class="btn btn-primary btn-block" value="Submit" onClick={this.handleSubmit}>Submit</button>
      </form>
    </div>
    </div>
        <div>
        <Link to= "/"><button type="button" class="btn btn-primary btn-block" >Home</button></Link>
    </div>
    </div>
    
/*Cierre de la funcion no tocar */
    )
  }
}

export default Form
