import {Link} from 'react-router-dom';
import React, {Component} from 'react';



class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

export default function Form(){
    const [formMovement, setFormMovement] = useState([]);
    useEffect(()=>{
        fetch('/movements/api').then((result)=>result.json()).then((json)=>{
            console.log({json});
            setFormMovement(json);
        }).catch(console.log);
    }, []);

    return (<div>
    <div class="container">
    <div class="row justify-content-center">
        <div class="col-6">
            <form action="/movements/api" method="post">
                <div class="form-group">
                    <label for="concept">Concept:</label>
                    <input type="text" name="concept" id="concept" class="form-control"/>
                </div>
                <div class="form-group">
                    <label for="amount">Amount:</label>
                        <div class="input-group-prepend">
                            <span class="input-group-text">$</span>
                            <input type="number" name="amount" id="amount" class="form-control"/>
                        </div>
                </div>
                <div class="form-group">
                    <label for="date">Date:</label>
                    <input type="date" name="date" id="date" class="form-control"/>
                </div>
                <div class="form-group">
                    <label for="type">Type:</label>
                    <select class="form-control" id="type" name="type">
                        <option>Income</option>
                        <option>Expense</option>
                    </select>
                </div>

                {/* POST form */}
                <button type="submit" class="btn btn-primary btn-block" onClick={()=>{
                        fetch('movements/api/postForm').then((result)=>result.json()).then((json)=>{
                            console.log({json});
                            setFormMovement(json);
                        }).catch(console.log);
                }}>Aceptar</button>

                {/* Back to home */}
                <div>
                    <Link to= "/"><button type="button" class="btn btn-primary btn-block">Volver a Home</button></Link>
                </div>
            </form>
        </div>
    </div>
</div>
</div>)};
