import {Link} from 'react-router-dom';
import React, {useState, useEffect} from 'react';
export default function Home(){
    const [movements, setMovements] = useState([]);
    useEffect(()=>{
        fetch('/movements/api').then((result)=>result.json()).then((json)=>{
            console.log({json});
            setMovements(json);
        }).catch(console.log);
    }, []);

    return (<div>
        <h1>App de finanzas</h1>

        {/* Balance */}
            <h2>Balance neto:</h2>

        {/* Last 10 movements */}
            <h2>Ultimos movimientos:</h2>
            <div>
                <div class="table-responsive">
                    <table class="table table-borderless ">
                        <thead>
                            <tr>
                                <th scope="col">Fecha</th>
                                <th scope="col">Concepto</th>
                                <th scope="col">Valor</th>
                                <th scope="col"></th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <th scope="row">1</th>
        <td>Sit</td>
        <td>Amet</td>
        <td>Consectetur</td>
      </tr>
      <tr>
        <th scope="row">2</th>
        <td>Adipisicing</td>
        <td>Elit</td>
        <td>Sint</td>
      </tr>
      <tr>
        <th scope="row">3</th>
        <td>Hic</td>
        <td>Fugiat</td>
        <td>Temporibus</td>
      </tr>
    </tbody>

  </table>
</div>
                {movements.map(aMovement=>
                <div>{aMovement.input_date.split("T")[0]},{aMovement.concept},{aMovement.amount}, {aMovement.input_type}</div>)}
            </div>
        
        {/* Buttons */}
        <div>
            <Link to=  "/form" ><   button type="button" class="btn btn-primary btn-block">Ingresar movimiento</button></Link>
            <br/>    
            <Link to="/movements"><button type="button" class="btn btn-primary btn-block">Ver movimientos</button></Link>
        </div>
    </div>);
};
