import {Link} from 'react-router-dom';
import React, {Component} from 'react';


class Movements extends Component {
    constructor() {
        super();
        this.state = { items: [] };
        this.state= {movementsList:[]}
    }

    componentDidMount() {
        fetch('movements/api/')
        .then(res => res.json())
        .then(data => {
          console.log(data);
          this.setState({
            movementsList:data
          })
        })
        .catch(console.log)
      }

    render() {
        return (
            <div className="container">

            {/* All movements */}
            <h2>Movimientos:</h2>
            {this.state.movementsList.map((aMovement) => (
                    <div class="row justify-content-center">     
                    <div class="col-8">
                        <table class="table table-borderless table table-hover">
                        <thead>
                            <tr>
                                <th scope="col">Date</th>
                                <th scope="col">Concept</th>
                                <th scope="col">Amount</th>
                                <th scope="col">Type</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>  
                                <td>
                                    {aMovement.input_date.split("T")[0].split('-').reverse().join('/')}
                                </td>
                                <td>
                                    {aMovement.concept}
                                </td>
                                <td>
                                    {aMovement.amount}
                                </td>
                                <td>
                                    {aMovement.input_type}
                                </td>
                            </tr>
                        </tbody>
                        </table>
                    </div>
                    </div>
            ))}
            </div>
        )}
}
    
export default Movements
